import { Typography } from 'antd';
import { Link, useLocation } from 'react-router-dom';
import NavDropDown from './NavDropDown';
import styles from './style.module.css';
import { useEffect, useState } from 'react';
const { Text } = Typography;

function NavMenu() {
  const location = useLocation();
  const [color, setColor] = useState('text-white');

  const menu = [
    {
      title: 'Thực đơn',
      url: '/thuc-don',
    },
    {
      title: 'Sảnh tiệc',
      url: '/sanh-tiec',
      childrens: [
        {
          title: 'Thuyển rồng Kim Long - Hoàng Long',
          url: '/thuyen-rong-kim-long-hoang-long',
        },
        {
          title: 'Sảnh Ngân Sen',
          url: '/sanh-ngan-sen',
        },
        {
          title: 'Sảnh Hoàng Sen - Kim Sen',
          url: 'sanh-hoang-sen-kim-sen',
        },
        {
          title: 'Sảnh Thanh Sen',
          url: 'sanh-thanh-sen',
        },
      ],
    },
    {
      title: 'Phòng hội nghị',
      url: '/phong-hoi-nghi',
    },
    {
      title: 'Khuyến mãi tiệc cưới',
      url: '/khuyen-mai-tiec-cuoi',
    },
  ];

  const [curentUrl, setCurentUrl] = useState('');

  useEffect(() => {
    setCurentUrl('/' + location.pathname.split('/')[1]);
  }, [location]);
  useEffect(() => {
    setColor(curentUrl === '/thuc-don' ? 'text-dark' : 'text-white');
  }, [curentUrl]);

  return (<>
    <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
      {
        menu.map((e) => (
          <li className="nav-item px-3">
            {
              e.childrens !== undefined ? <NavDropDown {...e} /> :
                <Link to={e.url}
                  className={`nav-link px-3 ${color} ${styles.menuItem} ${curentUrl === e.url ? styles.active : styles.hover}`}
                  aria-current="page"
                >
                  {e.title}
                </Link>
            }
          </li>
        ))
      }
    </ul >
  </>);
}
export default NavMenu;
