import React, { useState } from 'react';
import { Layout, Menu, Drawer, Button } from 'antd';
import { HomeOutlined, AppstoreOutlined, SearchOutlined, UserOutlined, MenuOutlined } from '@ant-design/icons';

const { Header } = Layout;

const App = () => {
  const [visible, setVisible] = useState(false);

  const showDrawer = () => {
    setVisible(true);
  };

  const onClose = () => {
    setVisible(false);
  };

  return (
    <Layout>
      <Header style={{ background: '#001529', padding: 0 }}>
        <div className="logo" style={{ color: 'white', fontSize: '20px', paddingLeft: '20px', float: 'left' }}>
          <h3 style={{ color: 'white', margin: 0 }}>My Website</h3>
        </div>

        {/* Button to open drawer on small screen */}
        <Button 
          type="primary" 
          icon={<MenuOutlined />} 
          onClick={showDrawer} 
          style={{ background: '#001529', color: 'white', float: 'right', display: 'none' }} 
          className="menu-button"
        />

        {/* Menu for large screens */}
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['1']} style={{ float: 'right' }}>
          <Menu.Item key="1" icon={<HomeOutlined />}>Home</Menu.Item>
          <Menu.Item key="2" icon={<AppstoreOutlined />}>Services</Menu.Item>
          <Menu.Item key="3" icon={<SearchOutlined />}>Search</Menu.Item>
          <Menu.Item key="4" icon={<UserOutlined />}>Profile</Menu.Item>
        </Menu>

        {/* Drawer for small screens */}
        <Drawer
          title="Navigation"
          placement="right"
          closable={true}
          onClose={onClose}
          visible={visible}
          width={250}
        >
          <Menu mode="inline" defaultSelectedKeys={['1']}>
            <Menu.Item key="1" icon={<HomeOutlined />}>Home</Menu.Item>
            <Menu.Item key="2" icon={<AppstoreOutlined />}>Services</Menu.Item>
            <Menu.Item key="3" icon={<SearchOutlined />}>Search</Menu.Item>
            <Menu.Item key="4" icon={<UserOutlined />}>Profile</Menu.Item>
          </Menu>
        </Drawer>
      </Header>
    </Layout>
  );
};

export default App;
