import { Table } from 'antd';
import type { TableProps } from 'antd';
import { useSelector } from 'react-redux';
import { RootState } from '../Redux/store';
import { useEffect, useState } from 'react';

interface DataType {
  address: string;
  area: number | null;
  contain: number | null;
  table: number | null;
  sesion: number | string | null;
  day: number | null;
  note: string | null;
}


function RoomPrice() {

  const room = useSelector((state: RootState) => state.room.val);
  const note = useSelector((state: RootState) => state.room.note);

  const [data, setData] = useState<DataType[]>([]);

  const columns: TableProps<DataType>['columns'] = [
    {
      title: 'Vị trí',
      dataIndex: 'address',
      rowScope: 'row',
    },
    {
      title: 'Diện tích',
      dataIndex: 'area',
      rowScope: 'row',
      render: (text) => <p>{text}m<sup>2</sup></p>,
    },
    {
      title: 'Xếp theo kiểu rạp hát',
      dataIndex: 'contain',
      rowScope: 'row',
      render: (text) => <p>{text} khách</p>,
    },
    {
      title: 'Công suất phòng',
      children: [
        {
          title: 'Xếp theo kiểu bàn tròn (10 khách/bàn)',
          dataIndex: 'table',
          rowScope: 'row',
        },
        {
          title: 'Xếp theo kiểu bàn tròn (10 khách/bàn)',
          dataIndex: 'table',
          rowScope: 'row',
        },
      ],
    },
    {
      title: 'Nữa ngày (VNĐ)',
      dataIndex: 'sesion',
      render: (text) => <p>{text.toLocaleString('vi-VN')}</p>,
      onCell: (e, i) => {
        switch (e.address) {
          case 'Thuyền Kim Long':
            return { colSpan: 3, rowSpan: 2 };
          case 'Thuyền Hoàng Long':
            return { colSpan: 0, rowSpan: 0 };
          default:
            return { colSpan: 1, rowSpan: 1 };
        }
      },
    },
    {
      title: 'Nguyên ngày (VNĐ)',
      dataIndex: 'day',
      render: (text) => <p>{text.toLocaleString('vi-VN')}</p>,
      onCell: (e, i) => {
        if (['Thuyền Kim Long', 'Thuyền Hoàng Long'].includes(e.address)) {
          return { rowSpan: 0, colSpan: 0 };
        }
        return { colSpan: 1, rowSpan: 1 };
      },
    },
    {
      title: 'Ghi chú',
      dataIndex: 'note',
      onCell: (_, i) => {
        return { rowSpan: i === 0 ? 5 : 0 };
      },
    },
  ];


  useEffect(() => {
    setData(
      room.map((e) => {
        let sesion: string | number | null = e.sesion;
        if (sesion === null) { sesion = 'Chỉ phục vụ set menu'; }
        return { ...e, note: note, sesion: sesion };
      }));
  }, []);

  return (<>
    <Table <DataType> columns={columns} dataSource={data} bordered />
  </>);
}
export default RoomPrice;
