import { Dropdown, List, MenuProps, Space, Typography } from 'antd';
import Item from 'antd/es/list/Item';
import { Link } from 'react-router-dom';

interface ItemDropDown {
  title: string,
  url: string,
}

const { Text } = Typography;

function NavMenu() {
  const toTag = (list: Array<ItemDropDown>) => {
    return list.map((e, i) => {
      return {
        label: <>
          <Link to={e.url}>{e.title}</Link>
        </>,
        key: i,
      };
    });
  };

  const menu = [
    {
      title: 'Thực đơn',
      url: '/thuc-don',
    },
    {
      title: 'Sảnh tiệc',
      url: '/sanh-tiec',
      children: [
        {
          title: 'Thuyển rồng Kim Long - Hoàng Long',
          url: '/thuyen-rong-kim-long-hoang-long',
        },
        {
          title: 'Sảnh Ngân Sen',
          url: '/sanh-ngan-sen',
        },
        {
          title: 'Sảnh Hoàng Sen - Kim Sen',
          url: 'sanh-hoang-sen-kim-sen',
        },
        {
          title: 'Sảnh Thanh Sen',
          url: 'sanh-thanh-sen',
        },
      ],
    },
    {
      title: 'Phòng hội nghị',
      url: '/phong-hoi-nghi',
    },
    {
      title: 'Khuyến mãi tiệc cưới',
      url: '/khuyen-mai-tiec-cuoi',
    },
  ];

  return (<>
    <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
      {
        menu.map((e) => e.children === undefined ? <>
          <li className="nav-item">
            <Link to={e.url} className="nav-link active" aria-current="page">{e.title}</Link>
          </li>
        </> : <>
          <li className="nav-item dropdown drop-hover">
            <Dropdown menu={{ items: toTag(e.children) }}>
              <a onClick={(ev) => ev.preventDefault()}>
                <Space>
                  {e.title}
                </Space>
              </a>
            </Dropdown>
          </li >
        </>)
      }
      <li className="nav-item dropdown drop-hover">
        <a className="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
          Dropdown
        </a>
        <ul className="dropdown-menu">
          <li><a className="dropdown-item" href="#">Action</a></li>
          <li><a className="dropdown-item" href="#">Another action</a></li>
          <li><hr className="dropdown-divider" /></li>
          <li><a className="dropdown-item" href="#">Something else here</a></li>
        </ul>
      </li>
    </ul >
  </>);
}
export default NavMenu;
