import React from 'react';
import styles from './style.module.css';

function HeaderBG({ title, img }) {

  const backgroundS: React.CSSProperties = {
    background: `url(${img})`,
    height: '50vh',
    backgroundPosition: 'center center',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
  };

  const darkLayer: React.CSSProperties = {
    backgroundColor: 'rgbaq(0, 0, 0, 0.5)',
    height: '50vh',
    paddingTop: '25vh',

  };

  return (<>
    <div className="container-fluid" style={backgroundS}>
      <div style={darkLayer}>
        <h1 className={`text-center ${styles.title}`}>{title}</h1>
      </div>
    </div>
  </>);
}
export default HeaderBG;
