import { Carousel, Image } from 'antd';
import React, { useState } from 'react';
import CarouItem from './CarouItem';

function Home() {

  const carouselBS: React.CSSProperties = {
    height: '100vh',
    position: 'relative',
  };

  const carouselCS: React.CSSProperties = {
    top: 0,
    left: 0,
    position: 'absolute',
  };

  const carouselConfig = {
    autoplay: true,
  };

  const [imgs, setImgs] = useState(['/images/carousel-1.jpg', '/images/carousel-2.jpg']);

  return (<>
    <div className='container-fluid' style={carouselBS}>
      <Carousel className='w-100 h-100' {...carouselConfig}>
        {imgs.map((e) => <CarouItem link={e} />)}
      </Carousel>
      
      <div className='w-100 h-100' style={carouselCS}>
        <h1>Home</h1>
      </div>
    </div>
  </>);
}
export default Home;
