import { Table } from 'antd';
import type { TableProps } from 'antd';
import { useSelector } from 'react-redux';
import { RootState } from '../Redux/store';
import { useEffect, useState } from 'react';

interface DataType {
  address: string;
  area: number | null;
  contain: number | null;
  table: number | null;
  sesion: number | null;
  day: number | null;
  note: string | null;
}


function RoomPrice() {

  const room = useSelector((state: RootState) => state.room.val);
  const note = useSelector((state: RootState) => state.room.note);

  const [data, setData] = useState<DataType[]>([]);

  const columns: TableProps<DataType>['columns'] = [
    {
      title: 'Vị trí',
      dataIndex: 'address',
      rowScope: 'row',
    },
    {
      title: 'Diện tích',
      dataIndex: 'area',
      rowScope: 'row',
    },
    {
      title: 'Xếp theo kiểu rạp hát',
      dataIndex: 'contain',
      rowScope: 'row',
    },
    {
      title: 'Công suất phòng',
      children: [
        {
          title: 'Xếp theo kiểu bàn tròn (10 khách/bàn)',
          dataIndex: 'table',
          rowScope: 'row',
        },
        {
          title: 'Xếp theo kiểu bàn tròn (10 khách/bàn)',
          dataIndex: 'table',
          rowScope: 'row',
        },
      ],
    },
    {
      title: 'Nữa ngày (VNĐ)',
      dataIndex: 'sesion',
      onCell: (e, i) => {
        return {
          colSpan: e.address === 'Thuyền Kim Long' ? 2 : 0,
          rowSpan: e.address === 'Thuyền Kim Long' ? 2 : 0,
        };
      },
    },
    {
      title: 'Nguyên ngày (VNĐ)',
      dataIndex: 'day',
      onCell: (e, i) => {
        if (['Thuyền Kim Long', 'Thuyền Hoàng Long'].includes(e.address)) {
          return { rowSpan: 0, colSpan: 0, };
        }
        return {
          colSpan: 1,
          rowSpan: 1,
        };
      },
    },
    {
      title: 'Ghi chú',
      dataIndex: 'note',
      onCell: (_, i) => {
        return { rowSpan: i === 0 ? 5 : 0 };
      },
    },
  ];


  useEffect(() => {
    setData(
      room.map((e) => {
        return { ...e, note: note };
      }));
  }, []);

  return (<>
    <Table <DataType> columns={columns} dataSource={data} bordered />
  </>);
}
export default RoomPrice;
