import React, { useEffect, useState } from 'react';
import { DownOutlined, SmileOutlined } from '@ant-design/icons';
import type { MenuProps } from 'antd';
import { ConfigProvider, Dropdown, Space } from 'antd';
import styles from './style.module.css';
import { Link } from 'react-router-dom';

interface ItemDropDown {
  title: string;
  url: string;
  childrens?: ItemDropDown[];
}

function NavDropDown({ title, url, childrens }: ItemDropDown) {
  const [items, setItems] = useState<MenuProps['items']>([]);

  useEffect(() => {
    setItems(
      JSON.parse(
        childrens?.map((e, i) =>
          JSON.stringify({
            key: i.toString(),
            label: (<>
              <Link to={e.url} className={` ${styles.dropItem}`}>{e.title}</Link>
            </>),
          })).join(JSON.stringify({
            type: 'divider',
          })))
    )
  }, []);

  return (<>
    <div className="nav-link">
      <ConfigProvider theme={{
        token: {
          controlItemBgHover: '#ff7a45',
          controlPaddingHorizontal: 20,
          fontSize: 17,
          padding: 0,
        },
        components: {
          Dropdown: {
            paddingBlock: 15,
          },
        },
      }} >
        <Dropdown menu={{ items }}>
          <a onClick={(e) => e.preventDefault()}>
            <Space>
              {title}
              <DownOutlined />
            </Space>
          </a>
        </Dropdown>
      </ConfigProvider>
    </div>

  </>);
}
export default NavDropDown;
